import React from 'react';
import { Square } from './Square';
import { StyleSheet, Text, View, Button, TouchableOpacity, TextInput } from 'react-native';
import { AddressSettings } from './Settings.js';

export function Selection() {
    let currentText = AddressSettings.address
    const setText = text => {
        currentText = text
    }

    const apply = text => {
        AddressSettings.address = text
    }


    return (
        <View className="AddressSettings-info" style={{ flexDirection: 'column' }}>
            <View style = {styles.container}>
                <TextInput style={styles.input_container}
                onChangeText={newText => setText(newText)}
                defaultValue={AddressSettings.address}>
            </TextInput>
            </View>
            <TouchableOpacity onPress={() => { apply(currentText) }} style={styles.button}>
                <Text style={styles.text}>Apply</Text>
            </TouchableOpacity>
        </View>
    );

}


const styles = {
    input_container: {
        textAlign: 'left',
        fontSize: 16,
        color: 'rgba(0,0,0,0.54)',
        fontWeight: 'bold',
        letterSpacing: 0.25,
        color: 'white',

    },
    container: {
        borderRadius:4,
        borderWidth: 4,
        borderColor:"white"

    },
    rectangle: {
        width: 50,
        height: 50,
        margin: 10,
        backgroundColor: "#338",
        transform: 'rotate(45deg)',
    },
    cont_row: {
        display: "flex",
        flexDirection: "row",
    },
    bruh: {
        position: 'absolute',
        width: 350,
        height: 350,
        margin: 35,
        backgroundColor: "rgba(255,255,0,1)",
    },
    check: {
        height: "50px",
        width: "50px",
        backgroundColor: "#555",
        borderRadius: "50%",
    }
}
