import React from 'react';
import { Square } from './Square';
import { Game } from './Game';
import { StyleSheet, Text, View, Button, TouchableOpacity, TextInput } from 'react-native';
export function Selection({ address }) {
    let currentText = Game.settings
    const setText = text => {
        // address(text)
        Game.settings = text
    }

    const apply = text => {
        // address(text)
        Game.settings = text
    }


    return (
        <View className="game-info" style={{ flexDirection: 'column' }}>
            <TextInput
                onChangeText={newText => setText(newText)}
                defaultValue={Game.settings}>
            </TextInput>

            <TouchableOpacity onPress={() => {apply()}} style={styles.button}>
                <Text style={styles.text}>Apply</Text>
            </TouchableOpacity>
        </View>
    );

}


const styles = {
    rectangle: {
        width: 50,
        height: 50,
        margin: 10,
        backgroundColor: "#338",
        transform: 'rotate(45deg)',
    },
    cont_row: {
        display: "flex",
        flexDirection: "row",
    },
    bruh: {
        position: 'absolute',
        width: 350,
        height: 350,
        margin: 35,
        backgroundColor: "rgba(255,255,0,1)",
    },
    check: {
        height: "50px",
        width: "50px",
        backgroundColor: "#555",
        borderRadius: "50%",
    }
}
