import { StyleSheet, Text, View, Button, TouchableOpacity } from 'react-native';
import { Game } from './Game'
import React from 'react'
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { LinearGradient } from 'expo-linear-gradient'
// import {Background} from './background.jsx'

// import AnimatedLinearGradient, {presetColors} from 'react-native-animated-linear-gradient'
// import {LinearGradient} from 'expo-linear-gradient';

function HomeScreen({ navigation }) {
  return (
    <LinearGradient start={{x: 0, y: 0.75}} end={{x: 1, y: 0.25}} colors={['#1FA2FF', '#12D8FA', '#A6FFCB']} style={{ flex: 1, alignItems: 'center', justifyContent: 'center',}}>
      <TouchableOpacity style={styles.button} onPress={() => navigation.navigate('4PL')}>
        <Text style={styles.text}>
          4 Players Local
        </Text>
      </TouchableOpacity>

      <TouchableOpacity style={styles.button} onPress={() => navigation.navigate('4PO')}>
        <Text style={styles.text}>
          4 Players Online
        </Text>
      </TouchableOpacity>

      <TouchableOpacity style={styles.button} onPress={() => navigation.navigate('2PL')}>
        <Text style={styles.text}>
          2 Players Local
        </Text>
      </TouchableOpacity>

      <TouchableOpacity style={styles.button} onPress={() => navigation.navigate('Mann')}>
        <Text style={styles.text}>
          Mann vs. Machine
        </Text>
      </TouchableOpacity>
    </LinearGradient>
  );
}

function PlayGame4PL() {
  return (
    <LinearGradient start={{x: 0, y: 0.75}} end={{x: 1, y: 0.25}} colors={['#1FA2FF', '#12D8FA', '#A6FFCB']} style={{ flex: 1, alignItems: 'center', justifyContent: 'center',}}>
       {/* <AnimatedLinearGradient customColors={presetColors.instagram} speed={4000}/> */}
      <Game mode={'4PL'} />
    </LinearGradient>
  );
}
function PlayGame2PL() {
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Game mode={'2PL'} />
    </View>
  );
}
function PlayGameMann() {
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Game mode={'Mann'} />
    </View>
  );
}
function PlayGame4PO() {
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Game mode={'4PO'} />
    </View>
  );
}

const Stack = createNativeStackNavigator();

export default function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="Home">
        <Stack.Screen name="Home" component={HomeScreen} />
        <Stack.Screen name="4PL" component={PlayGame4PL} />
        <Stack.Screen name="2PL" component={PlayGame2PL} />
        <Stack.Screen name="4PO" component={PlayGame4PO} />
        <Stack.Screen name="Mann" component={PlayGameMann} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  // background: {
  //   background: [linearGradient("135deg", hsla("224", "75%,", "45%", "1"), "0%", hsla("324", "75%", "45%", "1"), "100%")]
  // },
  linearGradient: {
    flex: 1,
    paddingLeft: 15,
    paddingRight: 15,
    borderRadius: 5
  },
  buttonText: {
    fontSize: 18,
    fontFamily: 'Gill Sans',
    textAlign: 'center',
    margin: 10,
    color: '#ffffff',
    backgroundColor: 'transparent',
  },
  turnBackground: {
    position: 'absolute',
    width: 425,
    height: 425,
    margin: 0
  },
  button: {
    alignItems: 'center',
    justifyContent: 'center',
    margin: 10,
    paddingVertical: 12,
    paddingHorizontal: 28,
    borderRadius: 4,
    elevation: 3,
    backgroundColor: 'black',
  },
  imageButton: {
    alignItems: 'center',
    justifyContent: 'center',
    margin: 10,
    padding: 5,
    borderRadius: 4,
    elevation: 3,
    backgroundColor: 'black',
  },
  text: {
    fontSize: 16,
    lineHeight: 21,
    fontWeight: 'bold',
    letterSpacing: 0.25,
    color: 'white',
  },
  container: {
    margin: 0,
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: 'linear-gradient(to bottom, #6be569, #6d92d2)',
  },
  linearGradient: {
    alignItems: 'center',
    justifyContent: 'center',
    borderRadius: 5,
    height: 500,
    width: 500,
  },
});
