import { StyleSheet, Text, View, Button } from 'react-native';
import { Game } from './Game'
import React from 'react'
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';

function HomeScreen({ navigation }) {
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Button
        title="4 Players Local"
        onPress={() => navigation.navigate('4P')}
      />
      <Button
        title="2 Players Local"
        onPress={() => navigation.navigate('2P')}
      />
    </View>
  );
}

function PlayGame4P() {
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Game mode={'4p'}/>
    </View>
  );
}
function PlayGame2P() {
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Game mode={'2p'}/>
    </View>
  );
}

const Stack = createNativeStackNavigator();

export default function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="Home">
        <Stack.Screen name="Home" component={HomeScreen} />
        <Stack.Screen name="4P" component={PlayGame4P} />
        <Stack.Screen name="2P" component={PlayGame2P} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  container: {
    margin: 0,
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: 'linear-gradient(to bottom, #6be569, #6d92d2)',
  },
  linearGradient: {
    alignItems: 'center',
    justifyContent: 'center',
    borderRadius: 5,
    height: 500,
    width: 500,
  },
});
