import React from 'react';
import { Square } from './Square';
import { StyleSheet, Text, View, Button, TouchableOpacity, TextInput } from 'react-native';
export class Selection extends React.Component {
    a = null
    constructor(props) {
        super(props);
        this.state = {
            input:props.address
        }
    }

    setText(text) {
        this.setState({
            input:text,
        })
    }

    render() {
        return (
            <View className="game-info" style={{ flexDirection: 'column' }}>
                <TextInput
                    onChangeText={newText => this.setText(newText)}
                    defaultValue={this.state.input}>
                </TextInput>
                
                <TouchableOpacity onPress={() => {}} style={styles.button}>
                    <Text style={styles.text}>Skip Turn</Text>
                </TouchableOpacity>
            </View>
        );
    }
}


const styles = {
    rectangle: {
        width: 50,
        height: 50,
        margin: 10,
        backgroundColor: "#338",
        transform: 'rotate(45deg)',
    },
    cont_row: {
        display: "flex",
        flexDirection: "row",
    },
    bruh: {
        position: 'absolute',
        width: 350,
        height: 350,
        margin: 35,
        backgroundColor: "rgba(255,255,0,1)",
    },
    check: {
        height: "50px",
        width: "50px",
        backgroundColor: "#555",
        borderRadius: "50%",
    }
}
